import React, { useEffect, useState } from 'react';
import axios from 'axios';

function Baza() {
  const [data, setData] = useState([]);
  const [username, setUsername] = useState('');
  const [email, setEmail] = useState('');

  useEffect(() => {
    // Pobierz dane z bazy danych po załadowaniu komponentu
    axios.get('http://localhost/get_data.php')
      .then(response => {
        console.log('Response data:', response.data);
        if (Array.isArray(response.data)) {
          setData(response.data);
        } else {
          console.error('Expected an array but got:', response.data);
        }
      })
      .catch(error => console.error('Error:', error));
  }, []);

  const handleSubmit = (e) => {
    e.preventDefault();

    if (!username || !email) {
      console.error('Username and email must be provided');
      return;
    }

    axios.post('http://localhost/add_user.php', {
      username,
      email
    })
    .then(response => {
      console.log('Response from server:', response.data);
      // Po dodaniu użytkownika odśwież dane
      axios.get('http://localhost/get_data.php')
        .then(response => {
          console.log('Response data:', response.data);
          if (Array.isArray(response.data)) {
            setData(response.data);
          } else {
            console.error('Expected an array but got:', response.data);
          }
        })
        .catch(error => console.error('Error:', error));
    })
    .catch(error => console.error('Error:', error));
  };

  return (
    <div>
      <h1>Dane z bazy:</h1>
      <ul>
        {data.map(item => (
          <li key={item.id}>{item.username} {item.email}</li>
        ))}
      </ul>
      <form onSubmit={handleSubmit}>
        <input 
          type="text" 
          placeholder="Username" 
          value={username} 
          onChange={(e) => setUsername(e.target.value)} 
        />
        <input 
          type="email" 
          placeholder="Email" 
          value={email} 
          onChange={(e) => setEmail(e.target.value)} 
        />
        <button type="submit">Dodaj użytkownika</button>
      </form>
    </div>
  );
}

export default Baza;
