import React, { useEffect, useState } from 'react';
import axios from 'axios';

function App() {
  const [users, setUsers] = useState([]);
  const [username, setUsername] = useState('');
  const [email, setEmail] = useState('');

  useEffect(() => {
    fetchUsers();
  }, []);

  // Funkcja do pobierania użytkowników
  const fetchUsers = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/users');
      setUsers(response.data);
    } catch (error) {
      console.error('Error fetching users:', error);
    }
  };

  // Funkcja do dodawania użytkownika
  const addUser = async () => {
    try {
      const response = await axios.post('http://localhost:5000/api/users', { username, email });
      setUsers([...users, response.data]);
      setUsername('');
      setEmail('');
    } catch (error) {
      console.error('Error adding user:', error);
    }
  };

  return (
    <div className="App">
      <h1>Users</h1>
      <ul>
        {users.map(user => (
          <li key={user.id}>
            {user.id} - {user.username} - {user.email} - {new Date(user.created_at).toLocaleDateString()}
          </li>
        ))}
      </ul>
      <div>
        <input
          type="text"
          placeholder="Username"
          value={username}
          onChange={e => setUsername(e.target.value)}
        />
        <input
          type="email"
          placeholder="Email"
          value={email}
          onChange={e => setEmail(e.target.value)}
        />
        <button onClick={addUser}>Add User</button>
      </div>
    </div>
  );
}

export default App;
